    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        String nombre,codigo;
        double valorh,horast,retencion,porcentaje;
        double salariob,salarion;
        
        nombre=txtNombre.getText();
        codigo=txtCodigo.getText();
        retencion=Integer.parseInt(txtRetencion.getText());
        valorh=Double.parseDouble(txtValorh.getText());
        horast=Double.parseDouble(txtHorast.getText());
        porcentaje=retencion/100;
        
        salariob=valorh*horast;
        salarion=salariob-((salariob*retencion)/100);
        
        lblMostrarNombre.setText("Nombres: "+nombre);
        lblMostrarCodigo.setText("C贸digo Empleado: "+codigo);
        lblMostrarSalarioNeto.setText("Salario Neto: "+salarion);
        lblMostrarSalarioBruto.setText("Salario Bruto: "+salariob);
        
    }//GEN-LAST:event_btnCalcularActionPerformed

